mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT lectureApplyNum, l.lectureNum, l.userId, userName, lectureName, lectureInfo, major,
				year,semester,day,time,l.regDate,midSDate,finSDate, NVL(a.status, 0) status
		FROM lecture l
		JOIN member m ON l.userId = m.userId
		JOIN lectureApply a ON l.lectureNum = a.lectureNum
		WHERE a.userId = #{userId}
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT l.lectureNum, v.videoFileNum, videoName, TO_CHAR(v.regDate, 'YYYY-MM-DD') regDate, videoTotalTime, 
				videoFileName, saveTime, p.status
		FROM lectureApply a
		JOIN lecture l ON a.lectureNum = l.lectureNum
		JOIN lectureVideoFile v ON l.lectureNum = v.lectureNum
		LEFT OUTER JOIN pauseSave p ON a.lectureApplyNum = p.lectureApplyNum
		WHERE l.lectureNum=#{lectureNum} AND a.userId=#{userId}
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT lectureNum, videoFileNum, videoTotalTime, videoName, videoFileName, regDate
FROM lectureVideoFile
WHERE videoFileNum=#{videoFileNum}

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT projectNum, lectureNum,projectSubject, TO_CHAR(regDate, 'YYYY-MM-DD') regDate, TO_CHAR(eDate, 'YYYY-MM-DD') eDate
		FROM project
		WHERE lectureNum=#{lectureNum}
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT p.projectsubject, s.originalFileName, s.submitNum, lectureApplyNum, TO_CHAR(s.submitDate, 'YYYY-MM-DD') submitDate FROM (SELECT * FROM project WHERE lectureNum = #{lectureNum}) p FULL OUTER JOIN (SELECT * FROM submit WHERE lectureApplyNum = #{lectureApplyNum} ) s ON p.projectNum = s.projectNum
p.projectNum=#{projectNum}
ORDER BY p.projectNum

mapper.update:
UPDATE submit SET originalFileName=#{originalFileName, jdbcType=VARCHAR}, 
				saveFileName=#{saveFileName, jdbcType=VARCHAR}, submitDate=SYSDATE
WHERE submitNum=#{submitNum}
