mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT l.lectureNum, l.userId, userName, lectureName, lectureInfo, major,
				year,semester,day,time,l.regDate,midSDate,finSDate, NVL(a.status, 0) status
		FROM lecture l
		JOIN member m ON l.userId = m.userId
		LEFT OUTER JOIN lectureApply a ON l.lectureNum = a.lectureNum
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
