mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT lectureNum, l.userId, userName, lectureName, lectureInfo, major,
				year,semester,day,time,l.regDate,midSDate,finSDate
		FROM lecture l
		JOIN member m ON l.userId = m.userId
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
